    public static int flag = 0;
    public void getNodes(Element node){
        System.out.println(flag++);
        System.out.println("--------------------");
        //当前节点的名称、文本内容和属性
        System.out.println("当前节点名称："+node.getName());//当前节点名称
        System.out.println("当前节点的内容："+node.getTextTrim());//当前节点名称
        List<Attribute> listAttr=node.attributes();//当前节点的所有属性的list
        for(Attribute attr:listAttr){//遍历当前节点的所有属性
            //if(attr.getName().equals("text") && !attr.getValue().equals("")) {
                String name = attr.getName();//属性名称
                String value = attr.getValue();//属性的值
                System.out.println("属性名称：" + name + " 属性值：" + value);
            //}
        }
        //递归遍历当前节点所有的子节点
        List<Element> listElement=node.elements();//所有一级子节点的list
        for(Element e:listElement){//遍历所有一级子节点
            this.getNodes(e);//递归
        }
    }

    public String getNodeByText(Document document, String xpath){
        String node = "";
        List<Element> composites = document.selectNodes(xpath);
        if(composites != null){
            for (Element e : composites) {
                //System.out.println(e.getName()+"="+e.getStringValue());
                node = e.getName();
            }
        }
        return node;
    }

    public boolean isExistNodeByText(Document document, String xpath){
        boolean isExist = false;
        List<Element> composites = document.selectNodes(xpath);
        if(composites != null && composites.size() > 0){
            isExist = true;
            for (Element e : composites) {
                System.out.println(e.getName()+"="+e.getStringValue());
            }
        }
        System.out.println(isExist);
        return isExist;
    }

